import UIKit

protocol FavouritesDisplayLogic: AnyObject
{
    func displayFavourites(viewModel: Favourites.GetFavourites.ViewModel)
    func displayDeleteFavourite(viewModel: Favourites.DeleteFavourite.ViewModel)
}

class FavouritesViewController: UIViewController, FavouritesDisplayLogic
{
    var interactor: FavouritesBusinessLogic?
    var router: (NSObjectProtocol & FavouritesRoutingLogic & FavouritesDataPassing)?
    
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
    {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder)
    {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup()
    {
        let viewController = self
        let interactor = FavouritesInteractor()
        let presenter = FavouritesPresenter()
        let router = FavouritesRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?)
    {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    // MARK: View lifecycle
    override func viewDidLoad(){
        super.viewDidLoad()
        setupFavouriteTableView()
        getFavourites()
    }
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        getFavourites()
    }
    
    
    // MARK: IB Outlets
    @IBOutlet var favouriteTableView: UITableView!
    
    var loading: Bool = true
    
    var products: [ProductModel] = []
    
    var refreshControl = UIRefreshControl()
    
    var userSession: UserSessionWorker = UserSessionWorker()
    var loggedIn: Bool {
        return userSession.isLoggedIn()
    }
    
    func getFavourites()
    {
        if loggedIn {
            let request = Favourites.GetFavourites.Request()
            interactor?.getFavourites(request: request)
        } else {
            products = []
            favouriteTableView.reloadData()
        }
    }
    
    func displayFavourites(viewModel: Favourites.GetFavourites.ViewModel)
    {
        
        refreshControl.endRefreshing()
        
        if let error = viewModel.error {
            if !viewModel.offline {
                showError(title: "Favourite Error", error: error)
            }
        } else {
            loading = false
            products = viewModel.products
            favouriteTableView.reloadData()
        }
        
    }
    
    func displayDeleteFavourite(viewModel: Favourites.DeleteFavourite.ViewModel){
        if let error = viewModel.error {
            showError(title: "Favourite Error", error: error)
        }
    }
    
    @objc func refreshFavourites(){
        if loggedIn {
            getFavourites()
        } else {
            refreshControl.endRefreshing()
        }
    }
    
}

extension FavouritesViewController: UITableViewDataSource, UITableViewDelegate {
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return loading ? 1 : products.count
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        return loggedIn ? configureProductCell(indexPath: indexPath) : configureRequestLoginCell(indexPath: indexPath)
    }
    
    func configureRequestLoginCell(indexPath: IndexPath) -> RequestLoginCell {
        let cell = favouriteTableView.dequeueReusableCell(withIdentifier: "RequestLoginCell", for: indexPath) as! RequestLoginCell
        
        cell.titleLabel.text = "Favourites"
        cell.descriptionLabel.text = "Login to view your favourites."
        
        cell.loginButtonPressed = loginButtonPressed
        
        cell.selectionStyle = UITableViewCell.SelectionStyle.none
        return cell
    }
    
    func configureProductCell(indexPath: IndexPath) -> ProductCell {
        let cell = favouriteTableView.dequeueReusableCell(withIdentifier: "ProductCell", for: indexPath) as! ProductCell
        
        cell.loading = loading
        cell.product = loading ? nil : products[indexPath.row]
        cell.configureUI()
        
        cell.selectionStyle = UITableViewCell.SelectionStyle.none
        
        return cell
    }
    
    func setupFavouriteTableView(){
        let productCellNib = UINib(nibName: "ProductCell", bundle: nil)
        favouriteTableView.register(productCellNib, forCellReuseIdentifier: "ProductCell")

        let requestLoginCellNib = UINib(nibName: "RequestLoginCell", bundle: nil)
        favouriteTableView.register(requestLoginCellNib, forCellReuseIdentifier: "RequestLoginCell")
        
        favouriteTableView.delegate = self
        favouriteTableView.dataSource = self
        
        setupRefreshControl()
        displayTableViewSeperator()
    }
    
    func displayTableViewSeperator(){
        favouriteTableView.separatorStyle = loggedIn ? .singleLine : .none
    }
    
    func setupRefreshControl(){
        refreshControl.attributedTitle = NSAttributedString(string: "Pull To Refresh")
        refreshControl.addTarget(self, action: #selector(refreshFavourites), for: .valueChanged)
        favouriteTableView.addSubview(refreshControl)
    }
}

extension FavouritesViewController {
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        if !loading {
            router?.selectedProductID = products[indexPath.row].id
            router?.routeToShowProduct(segue: nil)
        }
    }
}

//MARK: - Editing Table

extension FavouritesViewController {
    
    func tableView(_ tableView: UITableView, commit editingStyle: UITableViewCell.EditingStyle, forRowAt indexPath: IndexPath) {
        
        if editingStyle == .delete {
            deleteFavourite(indexPath: indexPath)
        }
    }
    
    func tableView(_ tableView: UITableView, canEditRowAt indexPath: IndexPath) -> Bool {
        return true
    }
    
    func deleteFavourite(indexPath: IndexPath){
        let deleteProduct = products[indexPath.row]
        
        let request = Favourites.DeleteFavourite.Request(productID: deleteProduct.id)
        interactor?.deleteFavourite(request: request)
        
        products.remove(at: indexPath.row)
        favouriteTableView.deleteRows(at: [indexPath], with: .fade)
    }
}

extension FavouritesViewController: UserLoggedInProtocol {
    func loginButtonPressed(){
        router?.routeToLogin(segue: nil)
    }
    
    func userLoggedInSuccessfully(){
        getFavourites()
    }
}
