import UIKit

protocol FlyerListBusinessLogic
{
    func getFlyers(request: FlyerList.GetFlyers.Request)
    
    func flyerSelected(flyer: FlyerModel)
}

protocol FlyerListDataStore
{
    var selectedFlyer: FlyerModel? { get set }
    var storeID: Int { get set }
}

class FlyerListInteractor: FlyerListBusinessLogic, FlyerListDataStore
{
    var presenter: FlyerListPresentationLogic?
    var worker: FlyerWorker = FlyerWorker(flyerAPI: FlyerAPI())
    
    var storeID: Int = 1
    var selectedFlyer: FlyerModel?
    
    func getFlyers(request: FlyerList.GetFlyers.Request)
    {
        worker.getFlyers(storeID: storeID) { (flyers: [FlyerModel], error: String?) in
            let response = FlyerList.GetFlyers.Response(flyers: flyers, error: error)
            self.presenter?.presentFlyers(response: response)
        }
    }
    
    func flyerSelected(flyer: FlyerModel){
        self.selectedFlyer = flyer
    }
}
