import UIKit

protocol FlyerListPresentationLogic
{
    func presentFlyers(response: FlyerList.GetFlyers.Response)
}

class FlyerListPresenter: FlyerListPresentationLogic
{
    weak var viewController: FlyerListDisplayLogic?
    
    func presentFlyers(response: FlyerList.GetFlyers.Response)
    {
        let viewModel = FlyerList.GetFlyers.ViewModel(flyers: response.flyers, error: response.error)
        viewController?.displayFlyers(viewModel: viewModel)
    }
}
