import UIKit

protocol MonitoredProductsPresentationLogic
{
    func presentMonitoredProducts(response: MonitoredProducts.GetMonitoredProducts.Response)
    func presentDeletedMonitoredProduct(response: MonitoredProducts.DeleteMonitoredProduct.Response)
}

class MonitoredProductsPresenter: MonitoredProductsPresentationLogic
{
    weak var viewController: MonitoredProductsDisplayLogic?
    
    // MARK: Do something
    
    func presentMonitoredProducts(response: MonitoredProducts.GetMonitoredProducts.Response)
    {
        let viewModel = MonitoredProducts.GetMonitoredProducts.ViewModel(products: response.products, error: response.error, offline: response.offline)
        viewController?.displayMonitoredProducts(viewModel: viewModel)
    }
    
    func presentDeletedMonitoredProduct(response: MonitoredProducts.DeleteMonitoredProduct.Response){
        
    }
}
