import UIKit

protocol LoginBusinessLogic
{
    func login(request: Login.Login.Request)
}

protocol LoginDataStore
{
    //var name: String { get set }
}

class LoginInteractor: LoginBusinessLogic, LoginDataStore
{
    var presenter: LoginPresentationLogic?
    var worker: UserWorker = UserWorker(userAuth: UserAuthAPI())
    
    // MARK: Do something
    
    func login(request: Login.Login.Request)
    {
        let email = request.email
        let password = request.password
        let notificationToken = request.notificationToken

        worker.login(email: email, password: password, notificationToken: notificationToken) { (user: UserLoginModel?, error: String?) in
            self.presenter?.presentLogin(response: Login.Login.Response(error: error))
        }
    }
    
    func appleLogin(request: Login.AppleLogin.Request){
        
    }
}
