import UIKit

@objc protocol MonitoredProductsRoutingLogic
{
    func routeToShowProduct(segue: UIStoryboardSegue?)
}

protocol MonitoredProductsDataPassing
{
    var dataStore: MonitoredProductsDataStore? { get }
}

class MonitoredProductsRouter: NSObject, MonitoredProductsRoutingLogic, MonitoredProductsDataPassing
{
    weak var viewController: MonitoredProductsViewController?
    var dataStore: MonitoredProductsDataStore?
    
    // MARK: Routing
    
    func routeToShowProduct(segue: UIStoryboardSegue?)
    {
        if let segue = segue {
            let destinationVC = segue.destination as! ShowProductViewController
            var destinationDS = destinationVC.router!.dataStore!
            passDataToShowProduct(source: dataStore!, destination: &destinationDS)
        } else {
            let storyboard = UIStoryboard(name: "Main", bundle: nil)
            let destinationVC = storyboard.instantiateViewController(withIdentifier: "ShowProductViewController") as! ShowProductViewController
            var destinationDS = destinationVC.router!.dataStore!
            passDataToShowProduct(source: dataStore!, destination: &destinationDS)
            navigateToShowProduct(source: viewController!, destination: destinationVC)
        }
    }
    
    // MARK: Navigation
    
    func navigateToShowProduct(source: MonitoredProductsViewController, destination: ShowProductViewController)
    {
        source.show(destination, sender: nil)
    }
    
    // MARK: Passing data
    
    func passDataToShowProduct(source: MonitoredProductsDataStore, destination: inout ShowProductDataStore)
    {
        destination.productID = source.selectedProductID!
    }
}
