import UIKit

protocol ShowDescriptionBusinessLogic
{
  func doSomething(request: ShowDescription.GetDescription.Request)
}

protocol ShowDescriptionDataStore
{
  var description: String { get set }
}

class ShowDescriptionInteractor: ShowDescriptionBusinessLogic, ShowDescriptionDataStore
{
  var presenter: ShowDescriptionPresentationLogic?
  var worker: ShowDescriptionWorker?
  var description: String = ""
  
  // MARK: Do something
  
  func doSomething(request: ShowDescription.GetDescription.Request)
  {
    worker = ShowDescriptionWorker()
    worker?.doSomeWork()
    
    let response = ShowDescription.GetDescription.Response(description: description)
    presenter?.presentDescription(response: response)
  }
}
