import UIKit

class StoresTableViewController: UITableViewController
{
    override func viewDidLoad()
    {
        super.viewDidLoad()
        setupStoresTableView()
    }
    
    var storePressed: ( (Int) -> Void)? = nil
    
    var displayedStores: [ShowStoreResults.DisplayedStore] = []
    var stores: [StoreModel] = []
    
    var loading: Bool = false
}

extension StoresTableViewController {
    override func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return stores.count
    }
    
    override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        return configureStoreResultsCell(indexPath: indexPath)
    }
    
    func configureStoreResultsCell(indexPath: IndexPath) -> StoreResultCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "StoreResultCell", for: indexPath) as! StoreResultCell
        
        cell.loading = loading
        cell.store = loading ? nil : displayedStores[indexPath.row]
        cell.configureUI()
        
        cell.selectionStyle = UITableViewCell.SelectionStyle.none
        
        return cell
    }
    
    func setupStoresTableView(){
        let storeResultCellNib = UINib(nibName: "StoreResultCell", bundle: nil)
        tableView.register(storeResultCellNib, forCellReuseIdentifier: "StoreResultCell")
        
        tableView.delegate = self
        tableView.dataSource = self
    }
}

extension StoresTableViewController {
    override func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        if !loading {
            let store = stores[indexPath.row]
            storePressed(storeID: store.id)
        }
    }
    
    func storePressed(storeID: Int){
        if let storePressed = storePressed {
            storePressed(storeID)
        }
    }
}
