import UIKit

protocol DeviceStoragePresentationLogic
{
    func presentSomething(response: DeviceStorage.Something.Response)
}

class DeviceStoragePresenter: DeviceStoragePresentationLogic
{
    weak var viewController: DeviceStorageDisplayLogic?
    
    func presentSomething(response: DeviceStorage.Something.Response)
    {
        let viewModel = DeviceStorage.Something.ViewModel()
        viewController?.displaySomething(viewModel: viewModel)
    }
}
