import UIKit

@objc protocol HelpAndFeedbackRoutingLogic
{
    func routeToFeedback(segue: UIStoryboardSegue?)
}

protocol HelpAndFeedbackDataPassing
{
    var dataStore: HelpAndFeedbackDataStore? { get }
}

class HelpAndFeedbackRouter: NSObject, HelpAndFeedbackRoutingLogic, HelpAndFeedbackDataPassing
{
    weak var viewController: HelpAndFeedbackViewController?
    var dataStore: HelpAndFeedbackDataStore?
    
    // MARK: Routing
    
    func routeToFeedback(segue: UIStoryboardSegue?)
    {
        if let segue = segue {
            let destinationVC = segue.destination as! FeedbackViewController
            var destinationDS = destinationVC.router!.dataStore!
            passDataToFeedback(source: dataStore!, destination: &destinationDS)
        } else {
            let storyboard = UIStoryboard(name: "Main", bundle: nil)
            let destinationVC = storyboard.instantiateViewController(withIdentifier: "FeedbackViewController") as! FeedbackViewController
            var destinationDS = destinationVC.router!.dataStore!
            passDataToFeedback(source: dataStore!, destination: &destinationDS)
            navigateToFeedback(source: viewController!, destination: destinationVC)
        }
    }
    
    // MARK: Navigation
    
    func navigateToFeedback(source: HelpAndFeedbackViewController, destination: FeedbackViewController)
    {
        source.show(destination, sender: nil)
    }
    
    // MARK: Passing data
    
    func passDataToFeedback(source: HelpAndFeedbackDataStore, destination: inout FeedbackDataStore)
    {
        destination.setting = source.selectedSetting
    }
}
