import UIKit

protocol PeopleBusinessLogic
{
  func doSomething(request: People.Something.Request)
}

protocol PeopleDataStore
{
  //var name: String { get set }
}

class PeopleInteractor: PeopleBusinessLogic, PeopleDataStore
{
  var presenter: PeoplePresentationLogic?
  var worker: PeopleWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: People.Something.Request)
  {
    worker = PeopleWorker()
    worker?.doSomeWork()
    
    let response = People.Something.Response()
    presenter?.presentSomething(response: response)
  }
}
