import UIKit

protocol ReportIssuePresentationLogic
{
  func presentSendIssue(response: ReportIssue.SendIssue.Response)
}

class ReportIssuePresenter: ReportIssuePresentationLogic
{
  weak var viewController: ReportIssueDisplayLogic?
  
  // MARK: Do something
  
  func presentSendIssue(response: ReportIssue.SendIssue.Response)
  {
    let viewModel = ReportIssue.SendIssue.ViewModel(error: response.error)
    viewController?.displaySendIssue(viewModel: viewModel)
  }
}
