import UIKit

protocol SettingsDisplayLogic: AnyObject
{
    func displayUserStore(viewModel: Settings.GetStore.ViewModel)
    func displayUserDetails(viewModel: Settings.GetUserDetails.ViewModel)
    func displayUpdateNotifications(viewModel: Settings.UpdateNotifications.ViewModel)
    func displayedLogout(viewModel: Settings.Logout.ViewModel)
    func displayedDeleted(viewModel: Settings.Delete.ViewModel)
}

class SettingsViewController: UIViewController, SettingsDisplayLogic
{
    
    var interactor: SettingsBusinessLogic?
    var router: (NSObjectProtocol & SettingsRoutingLogic & SettingsDataPassing)?
    
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
    {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder)
    {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup()
    {
        let viewController = self
        let interactor = SettingsInteractor()
        let presenter = SettingsPresenter()
        let router = SettingsRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?)
    {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    // MARK: View lifecycle
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        setupSettingsTableView()
        displayBarButton()
        getSettings()
    }
    
    let spinner: SpinnerViewController = SpinnerViewController()
    
    var displayUserSections: [Settings.DisplayUserSection] = []
    
    var refreshControl = UIRefreshControl()
    
    @IBOutlet var settingsTableView: UITableView!
    
    var userSession: UserSessionWorker = UserSessionWorker()
    var loggedIn: Bool {
        return userSession.isLoggedIn()
    }
    
    func getSettings()
    {
        let request = Settings.GetUserDetails.Request()
        interactor?.getSettings(request: request)
    }
    
    func displayUserDetails(viewModel: Settings.GetUserDetails.ViewModel)
    {
        displayUserSections = viewModel.displayUserSections
        settingsTableView.reloadData()
    }
    
    func getUserStore(){
        let request = Settings.GetStore.Request()
        interactor?.getUserStore(request: request)
    }
    
    func displayUserStore(viewModel: Settings.GetStore.ViewModel) {
        //        storeLoggedOutNameLabel!.text = viewModel.storeName
    }
    
    func displayedLogout(viewModel: Settings.Logout.ViewModel) {
        stopLoading()
        showRightBarButton()
        
        if let error = viewModel.error {
            showError(title: "Logout Error", error: error)
        } else {
            getSettings()
        }
    }
    
    func displayedDeleted(viewModel: Settings.Delete.ViewModel) {
        stopLoading()
        showRightBarButton()
        
        if let error = viewModel.error {
            showError(title: "Delete Error", error: error)
        } else {
            getSettings()
        }
    }
    
    func displayUpdateNotifications(viewModel: Settings.UpdateNotifications.ViewModel) {
        if let error = viewModel.error {
            showError(title: "Settings Error", error: error)
        }
    }
}

extension SettingsViewController {
    private func deleteAccount(){
        let refreshAlert = UIAlertController(title: "Delete Account", message: "Are you sure you want to delete your account. All your data will be permanently lost.", preferredStyle: UIAlertController.Style.alert)
        
        refreshAlert.addAction(UIAlertAction(title: "Yes, delete", style: .default, handler: { (action: UIAlertAction!) in
            self.startLoading()
            self.hideRightBarButton()
            
            let request = Settings.Delete.Request()
            self.interactor?.delete(request: request)
        }))
        
        refreshAlert.addAction(UIAlertAction(title: "Cancel", style: .cancel, handler: { (action: UIAlertAction!) in
            print("Cancel Delete")
        }))
        
        present(refreshAlert, animated: true, completion: nil)
    }
    
    @IBAction func deleteButtonPressed(_ sender: Any) {
        deleteAccount()
    }
    
}

extension SettingsViewController: UITableViewDataSource, UITableViewDelegate {
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return displayUserSections[section].settings.count
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        return configureSettingCell(indexPath: indexPath)
    }
    
    func configureSettingCell(indexPath: IndexPath) -> SettingCell {
        let cell = settingsTableView.dequeueReusableCell(withIdentifier: "SettingCell", for: indexPath) as! SettingCell
        
        let setting = displayUserSections[indexPath.section].settings[indexPath.row]
        
        cell.setting = setting
        cell.notificationSwitchPressedCallback = notificationSwitchPressed
        
        cell.configureUI()
        
        cell.selectionStyle = UITableViewCell.SelectionStyle.none
        
        return cell
    }
    
    func setupSettingsTableView(){
        let settingCellNib = UINib(nibName: "SettingCell", bundle: nil)
        settingsTableView.register(settingCellNib, forCellReuseIdentifier: "SettingCell")
        
        settingsTableView.delegate = self
        settingsTableView.dataSource = self
    }
    
    //MARK: - Section Header
    func numberOfSections(in tableView: UITableView) -> Int {
        return displayUserSections.count
    }
    
    func tableView(_ tableView: UITableView, viewForHeaderInSection section: Int) -> UIView? {
        let view = UIView()
        view.backgroundColor = UIColor(named: "Grey.Clear")
        return view
    }
    
    func tableView(_ tableView: UITableView, heightForHeaderInSection section: Int) -> CGFloat {
        return section == 0 ? 0 : 40
    }
    
    
    //MARK: - Row Selected
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        let setting: SettingModel = displayUserSections[indexPath.section].settings[indexPath.row]
        settingPressed(setting: setting)
    }
    
    func settingPressed(setting: SettingModel){
        switch setting.type {
        case .name:
            namePressed()
        case .email:
            emailPressed()
        case .password:
            passwordPressed()
        case .region:
            regionPressed()
        case .store:
            storePressed()
        case .logout:
            logoutPressed()
        case .login:
            loginButtonPressed()
        case .delete:
            deleteAccount()
        case .helpFeedback, .help, .feedback, .feature, .issue:
            helpAndFeedbackPressed()
        case .notification:
            break

        }
    }
}

extension SettingsViewController {
    
    func displayBarButton(){
        loggedIn ? showRightBarButton() : hideRightBarButton()
    }
    
    func showRightBarButton(){
        navigationItem.rightBarButtonItem?.isEnabled = true
    }
    
    func hideRightBarButton(){
        navigationItem.rightBarButtonItem?.isEnabled = false
    }
}


extension SettingsViewController {
    func startLoading() {
        addChild(spinner)
        spinner.view.frame = view.frame
        view.addSubview(spinner.view)
        spinner.didMove(toParent: self)
    }
    
    func stopLoading(){
        spinner.willMove(toParent: nil)
        spinner.view.removeFromSuperview()
        spinner.removeFromParent()
    }
}

extension SettingsViewController {
    @objc func namePressed(){
        router?.routeToEditName(segue: nil)
    }
    
    @objc func emailPressed(){
        router?.routeToEditEmail(segue: nil)
    }
    
    @objc func passwordPressed(){
        router?.routeToEditPassword(segue: nil)
    }
    
    @objc func regionPressed(){
        router?.routeToEditRegion(segue: nil)
    }
    
    @objc func storePressed(){
        router?.routeToEditStore(segue: nil)
    }
    
    @objc func logoutPressed(){
        let refreshAlert = UIAlertController(title: "Logout", message: "Are you sure you want to logout? Your data won't be lost.", preferredStyle: UIAlertController.Style.alert)
        
        refreshAlert.addAction(UIAlertAction(title: "Yes, logout", style: .default, handler: { (action: UIAlertAction!) in
            self.startLoading()
            self.hideRightBarButton()
            
            let request = Settings.Logout.Request()
            self.interactor?.logout(request: request)
        }))
        
        refreshAlert.addAction(UIAlertAction(title: "Cancel", style: .cancel, handler: nil))
        
        present(refreshAlert, animated: true, completion: nil)
    }
    
    
    @objc func helpAndFeedbackPressed(){
        router?.routeToHelpAndFeedback(segue: nil)
    }
}

extension SettingsViewController {
    func notificationSwitchPressed(sendNotifications: Bool) {
        
        if sendNotifications {
            var errorMessage: String? = nil
            
            let center = UNUserNotificationCenter.current()
            center.requestAuthorization(options: [.alert, .sound, .badge]) { granted, error in
                // Enable or disable features based on the authorization.
                if !granted {
                    errorMessage = "Please enable notifications from your apple settings."
                } else if let error = error {
                    errorMessage = error.localizedDescription
                }
                
                DispatchQueue.main.async {
                    if let errorMessage = errorMessage {
                        self.setNotification(enabled: false)
                        self.showError(title: "Notification Error", error: errorMessage)
                    } else {
                        let request = Settings.UpdateNotifications.Request(sendNotifications: sendNotifications)
                        self.interactor?.updateNotification(request: request)
                    }
                }
            }
            
        } else {
            let request = Settings.UpdateNotifications.Request(sendNotifications: sendNotifications)
            self.interactor?.updateNotification(request: request)
        }
    }
    
    func setNotification(enabled: Bool){
        for (section, userSection) in displayUserSections.enumerated() {
            for (row, setting) in userSection.settings.enumerated() {
                if setting.type == .notification {
                    let row = settingsTableView.cellForRow(at: IndexPath(row: row, section: section)) as! SettingCell
                    row.setNotification(enabled: enabled)
                }
            }
        }
    }
}


extension SettingsViewController: UserLoggedInProtocol {
    @IBAction func loginButtonPressed() {
        router?.routeToLogin(segue: nil)
    }
    
    func userLoggedInSuccessfully(){
        getSettings()
    }
}
