import UIKit

protocol VerifyCodeDisplayLogic: AnyObject
{
    func displayVerifiedCode(viewModel: VerifyCode.VerifyCode.ViewModel)
}

class VerifyCodeViewController: UIViewController, VerifyCodeDisplayLogic
{
    var interactor: VerifyCodeBusinessLogic?
    var router: (NSObjectProtocol & VerifyCodeRoutingLogic & VerifyCodeDataPassing)?
    
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
    {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder)
    {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup()
    {
        let viewController = self
        let interactor = VerifyCodeInteractor()
        let presenter = VerifyCodePresenter()
        let router = VerifyCodeRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?)
    {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad()
    {
        super.viewDidLoad()
    }
    
    // MARK: Outlets
    
    let spinner: SpinnerViewController = SpinnerViewController()
    
    @IBOutlet var codeField: UITextField!
    
    
    //MARK: - Display
    
    func displayVerifiedCode(viewModel: VerifyCode.VerifyCode.ViewModel)
    {
        stopLoading()
        
        if let error = viewModel.error {
            showError(title: "Verify Error", error: error)
        } else {
            router?.routeToNewPassword(segue: nil)
        }
    }
    
    //MARK: - Actions
    
    @IBAction func verifyCodeButtonPressed(_ sender: Any) {
        startLoading()
        dismissKeyboard()
        submitForm()
    }
    
    //MARK: - Extra
    
    private func submitForm(){
        let code = codeField.text ?? ""
        let request = VerifyCode.VerifyCode.Request(code: code)
        interactor?.verifyCode(request: request)
    }
    
    private func dismissKeyboard(){
        view.endEditing(true)
    }
}

extension VerifyCodeViewController {
    func startLoading() {
        addChild(spinner)
        spinner.view.frame = view.frame
        view.addSubview(spinner.view)
        spinner.didMove(toParent: self)
    }
    
    func stopLoading(){
        spinner.willMove(toParent: nil)
        spinner.view.removeFromSuperview()
        spinner.removeFromParent()
    }
}

extension VerifyCodeViewController: UITextFieldDelegate {
    
    private func setupTextFieldDelegate(){
        codeField.delegate = self
    }
    
    func textFieldShouldReturn(_ textField: UITextField) -> Bool
    {
        textField.resignFirstResponder()
        
        startLoading()
        dismissKeyboard()
        submitForm()
        
        return true
    }
}
