@testable import SuperStore
import XCTest

// Get Favourites
// Delete Favourties

class FavouritesInteractorTests: XCTestCase
{
    // MARK: Subject under test
    
    var sut: FavouritesInteractor!
    
    // MARK: Test lifecycle
    
    override func setUp()
    {
        super.setUp()
        setupFavouritesInteractor()
    }
    
    override func tearDown()
    {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupFavouritesInteractor()
    {
        sut = FavouritesInteractor()
    }
    
    // MARK: Test doubles
    
    class FavouritesPresentationLogicSpy: FavouritesPresentationLogic
    {
        
        var presentFavouriteCalled: Bool = false
        var presentDeleteFavouriteCalled: Bool = false
        
        func presentFavourites(response: Favourites.GetFavourites.Response) {
            presentFavouriteCalled = true
        }
        
        func presentDeleteFavourite(response: Favourites.DeleteFavourite.Response) {
            presentDeleteFavouriteCalled = true
        }
    }
    
    // MARK: Tests
    
    func testPresenterCalledWhenGetFavouritesCalled()
    {
        // Given
        let spy = FavouritesPresentationLogicSpy()
        sut.presenter = spy
        let request = Favourites.GetFavourites.Request()
        
        // When
        sut.getFavourites(request: request)
        
        // Then
        XCTAssertTrue(spy.presentFavouriteCalled, "doSomething(request:) should ask the presenter to format the result")
    }
}
